console.log("[TestPortal Helper] Background script loaded");chrome.runtime.onInstalled.addListener(()=>{console.log("[TestPortal Helper] Extension installed"),chrome.contextMenus.create({id:"testportal-helper-config",title:"TestPortal Helper Configuration",contexts:["all"]})});chrome.contextMenus.onClicked.addListener((e,o)=>{e.menuItemId==="testportal-helper-config"&&chrome.tabs.sendMessage(o.id,{action:"openConfig"},r=>{chrome.runtime.lastError?console.error("[TestPortal Helper] Error sending message:",chrome.runtime.lastError):console.log("[TestPortal Helper] Config window opened:",r)})});chrome.runtime.onMessage.addListener((e,o,r)=>(console.log("[TestPortal Helper] Message received:",e),!0));
